import cv2

def apply_threshold(image_path, out_image_path, threshold_value):
    # Read the image using OpenCV
    image = cv2.imread(image_path, 0)  # Read as grayscale

    # Apply thresholding
    _, thresholded_image = cv2.threshold(image, threshold_value, 255, cv2.THRESH_BINARY)

    # Save the thresholded images
    cv2.imwrite(out_image_path, thresholded_image)

# Example usage
image_path = '1.JPG'
threshold_value = 127  # Adjust the threshold value as needed
apply_threshold(image_path, 'tmp.jpg', threshold_value)